var fpCalc = function (op, x, y) {
    var xexp = Math.pow(10, String(x).length - String(x).indexOf('.') - 1),
        yexp = Math.pow(10, String(y).length - String(y).indexOf('.') - 1);
    var tens = Math.max(100, xexp, yexp);
    var n = 0;
    switch(op) {
        case '*':  n = x * y;
            break;
        case '-':  n = x - y;
            break;                
        case '+': n = x + y;
            break;
        case '/': n = x / y;
            break;
        default:  
            return NaN;
    }
    return Math.round(n * tens) / tens;
};

