                g.setColor(Color.white);
                g.fillOval(x, y, XSIZE, YSIZE);
                x += dx; y += dy;
                Dimension d = box.getSize();
                if (x < 0) { x = 0; dx = -dx; }                
                if (x + XSIZE >= d.width) { x = d.width - XSIZE; dx = -dx;}
                if (y < 0) { y = 0; dy = -dy; }
                if (y + YSIZE >= d.height) {y = d.height - YSIZE; dy = -dy; }

