#include <stdio.h>
void mat_mul(int a[100][100], int b[100][100], int c[100][100], int N)
{
	int i, j, k;
	for (i = 0; i < N; i++)
	{
		for (j = 0; i < N; j++)
		{
			for (k = 0; k < N; k++)
			{
				(*(*(c + i) + j)) += (*(*(a + i) + k)) * (*(*(b + k) + j));
			}
		}
	}
}
