void erase_blanks(char* str) {
	char* temp = (char*)malloc(sizeof(char)*BUFFER_LENGTH);
	int i = 0;
	int j = 0;
	while (str[i] != '\0') {
		if (str[i++] != ' ') {
			temp[j++] = str[i - 1];
		}
	}
	temp[j] = '\0';
	str = temp;
}

int main(void) {
	char* str = "aef aefe e ";
	erase_blanks(str);
	printf("%s", str);
        return 0;
    }
