#include<stdio.h>
#include<stdlib.h>

int idxsearch(int ar[], int first, int last, int target) {
	int mid = (first + last) / 2;
	if (first > last)
		return -1;
	else {
		if (ar[mid] == target)
			return mid;
		else {
			if (ar[mid] > target) {
				last = mid - 1;
				idxsearch(ar, first, last, target);
			}
			else {
				first = mid + 1;
				idxsearch(ar, first, last, target);
			}
		}
	}
}
